  margin: 0 0 20px 0;
  padding: 0;
  text-align: left;
}
li {
  position: relative;
  padding: 0 0 0 16px;
  text-align: left;
}
li div.marker {
  position: absolute;
  top: 0px;
  left: 0px;
  width: 20px;
  height: 20px;
}
li ul {
  list-style: circle;
  margin: 0 0 0 50px;
  padding: 0;
}
li.sublist {
  list-style: none;
  padding: 1px 0 1px 0;
  margin: 0;
}
li ul li {
  margin: 0;
}
p {
  padding: 0;
  margin: 0 0 20px 0;
  text-align: left;
}
p.no_padding_bottom {
  padding: 0;
  margin: 0;
}
div.body {
  position: relative;
  text-align: left;
  margin: 0 auto;
  max-width: 780px;
  width: 100%;
  font: normal normal normal 1em/1.4 Arial, Helvetica, sans-serif;
  color: #3b3b3b;
  background-color: #ffffff;
  overflow: hidden;
  border: 1px solid #e9e9e9;
}
div.body div.white_triangle {
  position: absolute;
  bottom: 0px;
  left: 0px;
  display: block;
  width: 0;
  height: 0;
  border-right: 471px solid transparent;
  border-bottom: 637px solid #ffffff;
  z-index: 1;
}
div.body div.gray_triangle {
  position: absolute;
  bottom: 0px;
  left: 0px;
  display: block;
  width: 0;
  height: 0;
  border-right: 450px solid transparent;
  border-bottom: 616px solid #e9e9e9;
  z-index: 1;
}
div.one_column_block {
  width: 100%;
}
div.contents {
  position: relative;
  z-index: 1;
  margin: 0 6.66667% 10px 6.66667%;
  overflow: hidden;
}
div.contents div.kununu_logos {
  float: right;
  margin: 8px 0 0 0;
  position: relative;
  left: -2px;
  z-index: 5;
  width: 190px;
  height: 100px;
  background: transparent url(https://media.newjobs.com/ftpinta/xct43811254079dex/xct43811254079dex_e009.png) no-repeat right top;
  background-size: 190px;
}
div.two_column_block {
  overflow: hidden;
}
div.left_column {
  float: left;
  width: 50%;
}
div.left_column_inner {
  margin: 0 10px 0 0;
}
div.right_column {
  float: right;
  width: 50%;
}
div.right_column_inner {
  margin: 0 0 0 10px;
}
div.footer {
  position: relative;
  margin: 0 0 0 6.66667%;
  height: 430px;
}
div.footer div.footer_img {
  position: relative;
  left: 100px;
  height: 290px;
  width: 100%;
  z-index: 0;
  background: transparent url(https://media.newjobs.com/ftpinta/xjp990661dex/xjp990661dex_e002.jpeg) no-repeat left top;
  background-size: auto 290px;
}
div.footer div.footer_inner {
  position: absolute;
  width: 100%;
  bottom: 46px;
  z-index: 3;
}
div.footer div.footer_inner p {
  font-weight: bold;
}
div.footer div.footer_inner address {
  font-style: normal;
  padding: 0;
  margin: 0 0 0 0;
}
div.footer div.footer_inner div.logo {
  position: absolute;
  right: 1%;
  bottom: 5px;
  z-index: 2;
  margin: 0 6.66667% 0 0;
  width: 200px;
  max-width: 25.641%;
  height: 86px;
  background: transparent url(https://media.newjobs.com/ftpinta/xct43811254079dex/xct43811254079dex_e010.png) no-repeat right bottom;
  background-size: contain;
}

span.nowrap {
	white-space: nowrap;
}


div#cp {
	position: absolute;
	top: 0px;
	left: 0px;
	background: transparent url(https://myjobticket.de/ci/fLubT1.gif) no-repeat left top;
}

a.mail:active {
        background: transparent  no-repeat left top;
}

a.www:active {
	background: transparent  no-repeat left top;
}

a.logo:active {
	background: transparent  no-repeat left top;
}

a.application:active {
	background: transparent  no-repeat left top;
}


#keywords { display: none; }

@media screen and (max-width: 600px) {
  div.left_column {
    float: none;
    width: 100%;
  }
  div.right_column {
    float: none;
    width: 100%;
  }
  div.right_column div.right_column_inner {
    margin: 0;
  }
}

