.UIButton.Button_Grey {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.5);
    border-radius: 10px;
    color: white;
    padding: 3px 10px;
    text-decoration: none;
}
.Button_Right button {
    background: none repeat scroll 0 0 rgba(255, 255, 255, 0.5);
    border: medium none;
    border-radius: 10px;
    bottom: 10px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
    cursor: pointer;
    padding: 2px 10px;
    position: absolute;
    right: 10px;
}
.row.Button_Section.clearfix {
    margin-top: 10px;
}

#btn-forward, #btn-apply, #btn-print {
    background-color: rgba(1,107,159,1);/*#2c3049;*/
    border-radius: 5px;
    border: 1px solid #949599;
    padding: 6px 12px;
	color:white;
	margin-right:5px;
	cursor:pointer;
	transition:0.3s;
}
#btn-forward:hover, #btn-apply:hover, #btn-print:hover {
    background-color: rgba(1,107,159,0.7);
	transition:0.3s;
}
.div-forward-mail {
	width:400px;
	background:rgba(55, 84, 126, 1);
	color:white;
	padding:10px;
	box-shadow: 0 0 3px rgba(255, 255, 255, 1);
}
.forwardHeader > h3 {
    border-bottom: 1px solid #ffffff;
    font-size: 1.1em;
    margin-bottom: 5px;
    padding-bottom: 10px;
}
.div-forward-mail .UIButton {
	background-color: rgba(255,255,255,0.7);
    border-radius: 5px;
    border: 1px solid #949599;
    padding: 6px 12px;
	color:white;
	margin-right:5px;
	cursor:pointer;
	transition:0.3s;
}
.div-forward-mail .UIButton:hover {
    background-color: rgba(1,107,159,0.7);
	transition:0.3s;
}
.div-forward-mail .UIButton[disabled] {
	cursor:not-allowed;
	transition:0.3s;
	background-color:rgba(1,107,159,0.2);
}
.div-forward-mail .UIButton[disabled]:hover {
	transition:0.3s;
	background-color:rgba(1,107,159,0.2);
}
.buttonsbox {
	text-align:center;	
	margin: 1% 0;
}
.hidden {
	display:none;
}
.forwardContent input {
    color: #333333;
}







